namespace StudioPress\Genesis\Tests\Unit;

use Brain\Monkey\Functions;
use \StudioPress\Genesis\Upgrade\Upgrade_DB_112 as Upgrader;

/**
 * Test Upgrade_DB_112.
 */
class Upgrade_DB_112_Test extends TestCase {
	/**
	 * Test upgrade method.
	 */
	public function test_upgrade() {
		Functions\when( 'genesis_get_option' )->alias(
			function( $arg ) {
				switch ( $arg ) {
					case 'header_full':
						return 0;
					default:
						return $arg;
				}
			}
		);

		Functions\expect( 'genesis_update_settings' )
			->once()
			->with(
				[
					'header_right'            => 1,
					'nav_superfish'           => 1,
					'subnav_superfish'        => 1,
					'nav_extras_enable'       => 1,
					'nav_extras'              => 'nav_right',
					'nav_extras_twitter_id'   => 'twitter_id',
					'nav_extras_twitter_text' => 'nav_twitter_text',
				]
			);

		( new Upgrader() )->upgrade();
	}
}
