namespace StudioPress\Genesis\Tests\Unit;

use Brain\Monkey\Functions;
use \StudioPress\Genesis\Upgrade\Upgrade_DB_130 as Upgrader;

/**
 * Test Upgrade_DB_130.
 */
class Upgrade_DB_130_Test extends TestCase {
	/**
	 * Test upgrade method.
	 */
	public function test_upgrade() {
		Functions\when( 'genesis_get_option' )->justReturn( 1 );
		Functions\when( 'genesis_get_seo_option' )->justReturn( 0 );

		Functions\expect( 'genesis_update_settings' )
			->once()
			->with(
				[
					'author_box_single' => 1,
				]
			);

		Functions\expect( 'genesis_update_settings' )
			->once()
			->with(
				[
					'noindex_cat_archive'    => 1,
					'noindex_tag_archive'    => 1,
					'noindex_author_archive' => 1,
					'noindex_date_archive'   => 1,
					'noindex_search_archive' => 1,
					'noodp'                  => 1,
					'noydir'                 => 1,
					'canonical_archives'     => 1,
				],
				GENESIS_SEO_SETTINGS_FIELD
			);

		Functions\expect( 'delete_transient' )->once()->with( 'genesis-remote-store' );

		( new Upgrader() )->upgrade();
	}
}
