namespace StudioPress\Genesis\Tests\Unit;

use Brain\Monkey\Functions;
use \StudioPress\Genesis\Upgrade\Upgrade_DB_1901 as Upgrader;

/**
 * Test Upgrade_DB_1901.
 */
class Upgrade_DB_1901_Test extends TestCase {
	/**
	 * Test upgrade method.
	 */
	public function test_upgrade() {
		$menu_locations = [
			'primary'   => 'something',
			'secondary' => 'something',
		];

		Functions\when( 'genesis_get_option' )->justReturn( 0 );
		Functions\when( 'get_theme_mod' )->justReturn( $menu_locations );

		Functions\expect( 'set_theme_mod' )
			->once()
			->with(
				'nav_menu_locations',
				[
					'primary'   => 0,
					'secondary' => 'something',
				]
			);

		Functions\expect( 'set_theme_mod' )
			->once()
			->with(
				'nav_menu_locations',
				[
					'primary'   => 0,
					'secondary' => 0,
				]
			);

		( new Upgrader() )->upgrade();
	}
}
