namespace StudioPress\Genesis\Tests\Unit;

use Brain\Monkey\Functions;
use \StudioPress\Genesis\Upgrade\Upgrade_DB_2207 as Upgrader;

/**
 * Test Upgrade_DB_2207.
 */
class Upgrade_DB_2207_Test extends TestCase {
	/**
	 * Test upgrade method with email.
	 */
	public function test_upgrade_email() {
		Functions\when( 'genesis_get_option' )->alias(
			function( $arg ) {
				switch ( $arg ) {
					case 'update_email':
						return 1;
					case 'update_email_address':
						return 'user@test.com';
				}
			}
		);

		Functions\expect( 'genesis_update_settings' )
			->once()
			->with(
				[
					'update_email'         => 'unset',
					'update_email_address' => 'user@test.com',
				]
			);

		( new Upgrader() )->upgrade();
	}

	/**
	 * Test upgrade method with no email.
	 */
	public function test_upgrade_no_email() {
		Functions\when( 'genesis_get_option' )->alias(
			function( $arg ) {
				switch ( $arg ) {
					case 'update_email':
						return 0;
					case 'update_email_address':
						return 'user@test.com';
				}
			}
		);

		Functions\expect( 'genesis_update_settings' )
			->once()
			->with(
				[
					'update_email'         => 'unset',
					'update_email_address' => '',
				]
			);

		( new Upgrader() )->upgrade();
	}
}
